    if(!alert) return null
    return (
        <div className={`alert alert-${type || 'secondary'} alert-dismissible fade show`} role="alert">
            {text}
            <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close"/>
        </div>
    );
};

